var myFilter = "";

if (myPayload.Category === "CAT_SYSLOG_0") {
    myCategory = "Syslog";
    //Calculate severity from PRI (=> PRI & 0x07) => needed for <filter>
    //          facility from PRI (=> PRI >> 3)   => not needed
    switch (myPayload.Number & 0x07) {
        case 0 :
            myFilter = "Emergency";
            break;
        case 1 :
            myFilter = "Alert";
            break;
        case 2 :
            myFilter = "Critical";
            break;
        case 3 :
            myFilter = "Error";
            break;
        case 4 :
            myFilter = "Warning";
            break;
        case 5 :
            myFilter = "Notice";
            break;
        case 6 :
            myFilter = "Informational";
            break;
        case 7 :
            myFilter = "Debug";
            break;
        default :
            myFilter = "Emergency";
            break;
    }
    node.warn (myFilter);
}
else if (myPayload.Category === "CAT_STATUS_1") {
    myCategory = "Status";
    switch (myPayload.Number >> 24) {
        case 0 :
            myFilter = "Good";
            break;
        case 0x40 :
            myFilter = "Uncertain";
            break;
        case 0x80 :
            myFilter = "Bad";
            break;
        default :
            myFilter = "Bad";
            break;
    }
    node.warn (myFilter);
}
else if (myPayload.Category === "CAT_NE107_2") {
    myCategory = "Ne107";
    switch (myPayload.Number) {
        case 0 :
            myFilter = "Normal";
            break;
        case 1 :
            myFilter = "Failure";
            break;
        case 2 :
            myFilter = "CheckFunction";
            break;
        case 3 :
            myFilter = "OutOfSpecification";
            break;
        case 4 :
            myFilter = "MaintenanceRequired";
            break;
        default :
            myFilter = "MaintenanceRequired";
            break;
    }
    node.warn (myFilter);
}
else if (myPayload.Category === "CAT_GENERIC_99") {
    myCategory = "Generic";
    myFilter = "High";
    node.warn (myFilter);
}
else {
    myCategory = "undefined";
    myFilter = "undefined";
    node.error (myFilter);
}

